<?php//Paramètres DEV-Mode_localif(LOCAL_MODE){	define('FR_STRFTIME_DEV_LOCAL_MODE',true);	setlocale(LC_TIME, 'fr_FR.utf8','fra');}else{	define('FR_STRFTIME_DEV_LOCAL_MODE',false);	setlocale(LC_TIME,"fr_FR");}/** * <p>Retourne une date formaté en français, identique à la fonction strftime()</p> * @param	(string)	$format -format de date à retourner => voir fonction strftime() @ http://be2.php.net/manual/fr/function.strftime.php * @param	(int)	$timestamp -(facultatif) timestamp de la date correspondante * @author nSun */function fr_strftime($format, $timestamp=false){	//Ajuste le format selon la config Windows (mode local)	if(FR_STRFTIME_DEV_LOCAL_MODE	&& strpos((string) $format, '%e') !== false)	{		$format = (string) str_replace('%e','%#d', (string) $format);	}	if($timestamp !== false)	{		$return = strftime($format, $timestamp);	}	else	{		$return = strftime($format);	}	return utf8_encode($return);}?>